using Microsoft.FeatureManagement;\n\npublic class SomeController : ControllerBase\n{\n    private readonly IFeatureManager _featureManager;\n\n    public SomeController(IFeatureManagerSnapshot featureManagerSnapshot)\n    {\n        _featureManager = featureManagerSnapshot;\n    }\n\n    [HttpGet]\n    public async Task<IActionResult> Get()\n    {\n        var ignoreValidation = await _featureManager.IsEnabledAsync("IgnoreValidation");\n        // Use ignoreValidation as needed\n        return Ok();\n    }\n}