// Obtain a reference to the remoting service\nIRemotingService remotingService = (IRemotingService)Activator.GetObject(\n    typeof(IRemotingService), "tcp://server:port/RemotingService");\n\npublic void PerformOperation() {\n    // Initiate the asynchronous operation\n    remotingService.PerformOperationAsync(null);\n    // Worker thread is now free to handle other requests\n}\n\nprotected async Task PerformOperationAsync() {\n    // Start an asynchronous task\n    var task = Task.Run(() => {\n        remotingService.PerformOperationAsync(null);\n    });\n\n    await task;\n\n    // Optionally, handle the result of the async operation\n}