using System;\nusing System.Web;\n\npublic class MyRestHandler : IHttpHandler {\n    public void ProcessRequest(HttpContext context) {\n        string method = context.Request.HttpMethod;\n        \n        switch (method.ToUpper()) {\n            case "GET":\n                HandleGet(context);\n                break;\n            \n            case "POST":\n                HandlePost(context);\n                break;\n                \n            // Add other methods like PUT, DELETE as needed\n            \n            default:\n                context.Response.StatusCode = 405; // Method Not Allowed\n                break;\n        }\n    }\n\n    private void HandleGet(HttpContext context) {\n        // Example of handling GET requests\n        string responseText = "This is a response to a GET request.";\n        context.Response.Write(responseText);\n    }\n\n    private void HandlePost(HttpContext context) {\n        // Example of handling POST requests\n        string responseText = "This is a response to a POST request.";\n        context.Response.Write(responseText);\n    }\n\n    public bool IsReusable { get { return false; } }\n}