// Use the using statement for proper disposal\npublic byte[] GenerateReport()\n{\n    string tempFilePath = Path.GetTempFileName();\n    try\n    {\n        using (FileStream fs = new FileStream(tempFilePath, FileMode.Create, FileAccess.Write, FileShare.None))\n        {\n            // Writing to the stream\n            // exampleData.CopyTo(fs);  // Assuming exampleData is a Stream object containing report data\n            fs.Close(); // Close the filestream once writing is done\n        }\n\n        // Read the contents of the file as a byte array for sending it back to the client\n        return File.ReadAllBytes(tempFilePath);\n    }\n    finally\n    {\n        // Delete the temporary file after use\n        File.Delete(tempFilePath);\n    }\n}