int[] userIds = { 1, 2, 3 }; // Example user IDs\nvar latestWorkplansForUsers = from w1 in db.Workplans\n                              where userIds.Contains(w1.UserID) &&\n                                    (from w2 in db.Workplans\n                                     where w2.UserID == w1.UserID && userIds.Contains(w2.UserID)\n                                     select w2.CreatedDate).Max() == w1.CreatedDate\n                              select w1;