using WatiN.Core;\n\npublic void TestWatinExample()\n{\n    // Start Internet Explorer and navigate to Google\n    using (IE ie = new IE("http://www.google.com"))\n    {\n        // Type 'WatiN' in the search box\n        ie.TextField(Find.ByName("q")).TypeText("WatiN");\n\n        // Click the 'Google Search' button\n        ie.Button(Find.ByName("btnK")).Click();\n\n        // Verify that results are showing up\n        Assert.IsTrue(ie.ContainsText("Waitn"));\n    }\n}