using (var db = new YourDataContext())\n{\n    var distinctCitiesByCountry = (from location in db.YourTableName\n                                   select new { Country = location.Country, City = location.City })\n                                   .Distinct()\n                                   .OrderBy(loc => loc.Country)\n                                   .ThenBy(loc => loc.City)\n                                   .ToList();\n\n    foreach (var item in distinctCitiesByCountry)\n    {\n        Console.WriteLine($"{item.Country}, {item.City}");\n    }\n}