usingMicrosoft.Practices.EnterpriseLibrary.Validation;\nusing System;\n\n[AttributeUsage(AttributeTargets.Property | AttributeTargets.Field | AttributeTargets.Parameter, AllowMultiple = false)]\npublic class CustomStringLengthValidatorAttribute : StringLengthValidatorAttribute\n{\n    // Specify the type of resource that contains validation messages here\n    public CustomStringLengthValidatorAttribute(int maxLength)\n        : base(maxLength)\n    {\n        MessageTemplateResourceType = typeof(Xltech.Common.Resources.XLStrings);\n    }\n}