private void webBrowser_Navigating(object sender, WebBrowserNavigatingEventArgs e)\n{\n    // Optionally log or prepare for navigation\n}\n\nprivate void webBrowser_DocumentCompleted(object sender, WebBrowserDocumentCompletedEventArgs e)\n{\n    // Check if the body contains an error message indicating a page not found\n    if (webBrowser.Document != null && \n        ((mshtml.HTMLDocument)webBrowser.Document.DomDocument).body != null)\n    {\n        string innerText = ((mshtml.HTMLBody) ((mshtml.HTMLDocument) webBrowser.Document.DomDocument).body).innerText;\n        if (!string.IsNullOrEmpty(innerText))\n        {\n            // Heuristically check for common strings in error pages\n            if (innerText.Contains("Page not found") || \n                innerText.Contains("Cannot display webpage"))\n            {\n                MessageBox.Show("Error: Page not found or cannot display.");\n            }\n        }\n    }\n}\n\nprivate void webBrowser_NavigateError(object sender, WebBrowserNavigateErrorEventArgs e)\n{\n    // Handle navigation errors\n    if (!e.IsHandeled)\n    {\n        MessageBox.Show($"Navigation Error: {e.Url}");\n        e.Cancel = true; // Cancel the default error dialog\n    }\n}