using System;\nusing System.Collections.Generic;\nusing System.DirectoryServices.AccountManagement;\n\npublic class ADGroupRetriever\n{\n    public List<string> GetGroups()\n    {\n        var groups = new List<string>();\n\n        // Create principal context using your domain details\n        using (var context = new PrincipalContext(ContextType.Domain))\n        {\n            // Create a group principal to search for all groups\n            var groupPrincipal = new GroupPrincipal(context);\n\n            // Search for all matching groups\n            using (var searcher = new PrincipalSearcher(groupPrincipal))\n            {\n                foreach (var found in searcher.FindAll())\n                {\n                    if (found is GroupPrincipal)\n                    {\n                        var group = (GroupPrincipal)found;\n                        groups.Add(group.Name);\n                    }\n                }\n            }\n        }\n\n        return groups;\n    }\n}