using System;\nusing System.Collections.Generic;\nusing System.Text;\n\npublic class Employee\n{\n    public int ID { get; set; }\n    public string Name { get; set; }\n}\n\npublic static class SqlStatementGenerator\n{\n    public static string[] GenerateInsertStatements(Employee[] employees)\n    {\n        List<string> sqlStatements = new List<string>();\n\n        foreach (var employee in employees)\n        {\n            // Escape single quotes by doubling them\n            string safeName = employee.Name.Replace("'", "''");\n\n            // Use StringBuilder for efficient string manipulation\n            var sb = new StringBuilder();\n            sb.AppendFormat("INSERT INTO Employees (id, name) VALUES ({0}, '{1}')", employee.ID, safeName);\n\n            sqlStatements.Add(sb.ToString());\n        }\n\n        return sqlStatements.ToArray();\n    }\n}