// Example of applying FullTrust in a security policy file or programmatically\nSystem.Security.AppDomainSetup setup = new System.Security.AppDomainSetup();\nsetup.ApplicationBase = appPath;\nAppDomain domain = AppDomain.CreateDomain("NewDomain", null, setup);\nPolicyLevel level = PolicyManager.GetMachinePolicyLevel();\nCodeGroup myCodeGroup = (UnionCodeGroup)level.RootCodeGroup;\nmyCodeGroup.AddChild(new StrongNameMembershipCondition(sn));\nmyCodeGroup.AddChild(new ZoneMembershipCondition(SecurityZone.MyComputer));\nEvidence ev = new Evidence();\nev.AddHostEvidence(new Zone(SecurityZone.MyComputer));\nPolicyStatement ps = new PolicyStatement(new PermissionSet(PermissionState.FullTrust));\nlevel.SetCachePolicy(ps);\nAppDomain.CurrentDomain.SetData("APP_DIRECTORY", appPath);