using NHibernate;\nusing NHibernate.Cfg;\n\nvar sessionFactory = ActiveRecordMediator.GetSessionFactory(typeof(Product));\nusing (ISession session = sessionFactory.OpenSession())\n{\n    var count = session.CreateQuery("select count(*) from Product p where p.Date > :date")\n                       .SetDateTime("date", yourDateValue)\n                       .UniqueResult<long>();\n    Console.WriteLine($"Number of products: {count}");\n}