public JsonResult GetCountries()\n{\n    var countries = new List<Country>\n    {\n        new Country { Id = 1, Name = "USA" },\n        new Country { Id = 2, Name = "Canada" }\n    };\n\n    return Json(countries, JsonRequestBehavior.AllowGet);\n}\n\npublic JsonResult GetCitiesByCountryId(int countryId)\n{\n    var cities = new List<City>\n    {\n        new City { Id = 1, Name = "New York", CountryId = 1 },\n        new City { Id = 2, Name = "Los Angeles", CountryId = 1 },\n        new City { Id = 3, Name = "Toronto", CountryId = 2 }\n    };\n\n    var filteredCities = cities.Where(c => c.CountryId == countryId).ToList();\n    return Json(filteredCities, JsonRequestBehavior.AllowGet);\n}