using System.IO;\nusing iText.Kernel.Pdf;\n\npublic void SetPdfProperties(string filePath)\n{\n    using (PdfReader reader = new PdfReader(filePath))\n    {\n        string outputFilePath = Path.Combine(Path.GetDirectoryName(filePath), "Modified_" + Path.GetFileName(filePath));\n        \n        // Initialize PDF document with the read PDF\n        using (PdfDocument pdfDoc = new PdfDocument(reader, new PdfWriter(outputFilePath)))\n        {\n            // Get the document information dictionary which holds metadata about the document\n            PdfDictionary infoDict = pdfDoc.GetPdfObject().GetAsDict(PdfName.Info);\n            \n            if (infoDict == null)\n            {\n                infoDict = new PdfDictionary();\n                pdfDoc.GetPdfObject().Put(new PdfName("Info"), infoDict);\n            }\n\n            // Set the properties\n            infoDict.Put(PdfName.Title, new PdfString("Your Title Here"));\n            infoDict.Put(PdfName.Author, new PdfString("Author Name"));\n            infoDict.Put(PdfName.Subject, new PdfString("Document Subject"));\n\n            // Update document metadata in XMP format if necessary (optional)\n            pdfDoc.Catalog.DocumentInfo.SetSubject("Document Subject");\n            pdfDoc.Catalog.DocumentInfo.SetTitle("Your Title Here");\n            pdfDoc.Catalog.DocumentInfo.SetAuthor("Author Name");\n\n            // Close the document\n            infoDict.Flush();\n        }\n    }\n}