class Program\n{\n    private static readonly HttpClient client = new HttpClient();\n\n    static async Task Main(string[] args)\n    {\n        // URL of your web service endpoint\n        string url = "http://yourwebsite.com/api/resourceId";\n\n        // Data you want to update, assuming it's JSON\n        string jsonData = "{\"key\":\"value\",\"anotherKey\":\"anotherValue\"}";\n\n        // Create StringContent for the body of the request\n        var jsonContent = new StringContent(jsonData, Encoding.UTF8, "application/json");\n\n        try\n        {\n            // HTTP PUT request\n            HttpResponseMessage response = await client.PutAsync(url, jsonContent);\n\n            if (response.IsSuccessStatusCode)\n            {\n                Console.WriteLine("The data was updated successfully.");\n            }\n            else\n            {\n                Console.WriteLine($"Failed to update the data. Status code: {response.StatusCode}");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An exception occurred: {ex.Message}");\n        }\n    }\n}