// Assuming this is your custom type and it needs initialization similar to MyType\npublic class CustomType\n{\n    public string Name { get; set; }\n    public decimal Value { get; set; }\n\n    // Add other properties if necessary\n\n    // Example constructor if needed\n    public CustomType(string name, decimal value)\n    {\n        Name = name;\n        Value = value;\n    }\n}\n\nDictionary<int, CustomType> dict;\n\ndict = (from t in table\n        select new {\n           id = t.Id,\n           ct = new CustomType { Name = t.Name, Value = t.Value }\n        }).ToDictionary(item => item.id, item => item.ct);