// Example of using Capstone for x86 disassembly in C#.\n   // To install Capstone.NET via NuGet: Install-Package Capstone.NET\n\n   using Capstone;\n\n   byte[] code = new byte[] { 0x83, 0xEC, 0x2C };\n   var engine = new Disassembler(Architecture.X86) {\n       Mode = DisassembleMode.Mode32\n   };\n\n   foreach (var ins in engine.Disasm(code, 0x1000)) {\n       // Analyze each instruction\n       Console.WriteLine($"{ins.Address.ToString("X")}:\t{ins.Mnemonic} {ins.OpStr}");\n   }