using System;\nusing System.Collections.Generic;\n\npublic struct Test\n{\n    public string x;\n    public List<string> y;\n\n    // Constructor to initialize the list\n    public Test(string initialString, List<string> initialList)\n    {\n        x = initialString;\n        y = initialList ?? new List<string>();\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        var myStruct = new Test("example", new List<string> { "item1", "item2" });\n        \n        // Accessing the list in the struct\n        Console.WriteLine(myStruct.x);\n        foreach (var item in myStruct.y)\n        {\n            Console.WriteLine(item);\n        }\n    }\n}