using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        // Define the first list\n        List<string> firstList = new List<string> { "apple", "banana", "cherry", "date" };\n\n        // Define the second list\n        List<string> secondList = new List<string> { "banana", "date" };\n\n        // Select items that are in the first list but not in the second list\n        var result = firstList.Except(secondList);\n\n        // Print the result\n        Console.WriteLine("Items in the first list but not in the second list:");\n        foreach (var item in result)\n        {\n            Console.WriteLine(item);\n        }\n    }\n}