using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main()\n    {\n        ManagementObjectSearcher searcher = new ManagementObjectSearcher("root\\SecurityCenter2", "SELECT * FROM AntiVirusProduct");\n\n        foreach (ManagementObject queryObj in searcher.Get())\n        {\n            Console.WriteLine("-------------------------");\n            Console.WriteLine("AntiVirusState: {0}", queryObj["displayName"]);\n            Console.WriteLine("Enabled: {0}", Convert.ToBoolean(queryObj["enabled"]));\n        }\n    }\n}