using System.Security.Cryptography.X509Certificates;\n\nclass Program\n{\n    static void Main()\n    {\n        // Using X509Certificate\n        var cert1 = new X509Certificate("path_to_certificate.cer");\n\n        // Using X509Certificate2\n        var cert2 = new X509Certificate2("path_to_certificate.pfx", "password");\n        \n        // Example of accessing more detailed information from X509Certificate2\n        Console.WriteLine(cert2.FriendlyName);\n    }\n}