using iText.Kernel.Pdf;\nusing iText.Forms.Fields;\nusing iText.Layout;\nusing System.IO;\n\npublic void FillPDF(string templatePath, string outputPath, Dictionary<string, string> fieldValues)\n{\n    using (PdfDocument pdfDoc = new PdfDocument(new PdfReader(templatePath), new PdfWriter(outputPath)))\n    {\n        PdfAcroForm form = PdfAcroForm.GetAcroForm(pdfDoc, true);\n        form.SetFields(fieldValues);\n    }\n}