using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class Account\n{\n    public string Code { get; set; }\n    // other properties...\n}\n\npublic class Detail\n{\n    public string Code { get; set; }\n    public int Eid { get; set; }\n    // other properties...\n}\n\npublic class AccEntry\n{\n    public int Eid { get; set; }\n    public DateTime EDate { get; set; }\n    public decimal Amount { get; set; }\n    // other properties...\n}\n\n// Assuming you have the following lists representing your tables.\nvar accounts = new List<Account>();\nvar details = new List<Detail>();\nvar accEntries = new List<AccEntry>();\n\nvar result = from a in accounts\n             join d in details on a.Code equals d.Code\n             join e in accEntries on d.Eid equals e.Eid\n             where e.EDate > new DateTime(2000, 1, 1)\n             group e by d.Code into g\n             select new { Code = g.Key, SumAmount = g.Sum(e => e.Amount) };