public void TestMethod1()\n{\n    WebTestRequest request = new WebTestRequest("http://example.com");\n    \n    // Add a validation rule to ensure a specific string exists in the response\n    request.ValidateResponse += (sender, e) => {\n        if (!e.Response.BodyString.Contains("expectedText")) {\n            throw new Exception("Expected text not found!");\n        }\n    };\n    \n    WebTestContext context = new WebTestContext();\n    WebTestResult result = request.Execute(context);\n}