using Syncfusion.Pdf;\nusing Syncfusion.Pdf.Interactive;\n\npublic void FillPDF(string templatePath, string outputPath, Dictionary<string, string> fieldValues)\n{\n    PdfDocument document = new PdfDocument(new FileStream(templatePath, FileMode.Open));\n    \n    foreach (var kvp in fieldValues)\n    {\n        PdfAcroForm AcroForm = document.Form;\n        PdfField field = AcroForm.Fields[kvp.Key];\n        if (field != null && field.FieldType == PdfFieldType.TextBox)\n            ((PdfTextBoxField)field).Text = kvp.Value;\n    }\n\n    document.Save(outputPath);\n    document.Close(true);\n}