ProcessStartInfo start = new ProcessStartInfo\n{\n    Arguments = "\"" + newTargetFile + "\" \"" + originalFile.FullName + "\"",\n    FileName = filename,\n    WindowStyle = ProcessWindowStyle.Hidden, // Hide the window if not needed\n    CreateNoWindow = true, // If you don't need a console window at all\n    UseShellExecute = false,\n    RedirectStandardOutput = true,\n    RedirectStandardError = true\n};\n\n// Run the external process & wait for it to finish\nusing (Process proc = Process.Start(start))\n{\n    string output = proc.StandardOutput.ReadToEnd();\n    string error = proc.StandardError.ReadToEnd();\n\n    // Output to your debugger or console\n    System.Diagnostics.Debug.WriteLine("Output: " + output);\n    System.Diagnostics.Debug.WriteLine("Errors: " + error);\n\n    proc.WaitForExit();\n}