using Google.Cloud.Translation.V2;\nusing System;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string text = "dog";\n        string translatedText = TranslateText(text, "en", "fr");\n        Console.WriteLine("Translated Text: " + translatedText);\n    }\n\n    public static string TranslateText(string text, string sourceLanguage, string targetLanguage)\n    {\n        var client = TranslationClient.Create();\n        var response = client.TranslateText(text, targetLanguage, sourceLanguage);\n        return response.TranslatedText;\n    }\n}