using System;\nusing System.Net;\nusing System.Security.Authentication;\nusing System.DirectoryServices.Protocols;\n\nclass LDAPExample\n{\n    static void Main()\n    {\n        string ldapServer = "ldap://example.com";\n        string username = "user@example.com";\n        string password = "password";\n\n        using (LdapConnection connection = new LdapConnection())\n        {\n            try\n            {\n                // Set the server details\n                connection.Connect(ldapServer);\n\n                NetworkCredential credential = new NetworkCredential(username, password);\n                SaslDigestMd5AuthenticationHelper authHelper =\n                    new SaslDigestMd5AuthenticationHelper(credential);\n\n                // Bind to the LDAP directory\n                connection.Bind(authHelper.GetPasswordAuthMechanism());\n\n                Console.WriteLine("Connected and bound successfully!");\n            }\n            catch (LdapException e)\n            {\n                Console.WriteLine($"LDAP Exception: {e.Message}");\n            }\n        }\n    }\n}