public void ProcessRequest(HttpContext context)\n{\n    string targetUrl = "http://dev/tracker/urchin.ashx" + context.Request.QueryString.ToString();\n    HttpWebRequest request = WebRequest.Create(targetUrl) as HttpWebRequest;\n    \n    // Copy all necessary headers, including cookies for authentication.\n    foreach (string key in context.Request.Headers.AllKeys)\n    {\n        if (!string.IsNullOrEmpty(key))\n            request.Headers[key] = context.Request.Headers[key];\n    }\n    \n    using (HttpWebResponse response = request.GetResponse() as HttpWebResponse)\n    using (StreamReader reader = new StreamReader(response.GetResponseStream()))\n    {\n        string responseData = reader.ReadToEnd();\n        // Optionally log or process the response data.\n        \n        // Pass on the response to the client if needed for some reason.\n        context.Response.Write(responseData);\n        context.Response.ContentType = response.ContentType;\n    }\n}