using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing Org.BouncyCastle.Crypto;\nusing Org.BouncyCastle.OpenSsl;\n\npublic class PemReaderExample\n{\n    public static void Main()\n    {\n        string pemString = File.ReadAllText("path/to/your/privatekey.pem");\n        \n        using (var reader = new StringReader(pemString))\n        {\n            var pemObject = new PemReader(reader).ReadObject();\n            AsymmetricCipherKeyPair keyPair;\n            \n            if (pemObject is RsaPrivateCrtKeyParameters rsaParams)\n            {\n                RSACryptoServiceProvider rsa = DotNetUtilities.ToRSA(rsaParams);\n                \n                // Now you can use the rsa object to decrypt data\n                Console.WriteLine("Successfully read and converted RSA private key.");\n                \n                // Example of using the RSA object:\n                byte[] encryptedData = Convert.FromBase64String("your_encrypted_data_here");\n                byte[] decryptedData = rsa.Decrypt(encryptedData, true);\n                string decryptedText = System.Text.Encoding.UTF8.GetString(decryptedData);\n                Console.WriteLine($"Decrypted Text: {decryptedText}");\n            }\n        }\n    }\n}