// Assuming 'ds' is your DataSet and 'YourTableName' is the name of the DataTable\nDataTable dataTable = ds.Tables["YourTableName"];\n\n// Sort the DataTable on a specific column, e.g., "ColumnName"\ndataTable.DefaultView.Sort = "ColumnName ASC"; // Use DESC for descending order\n\n// Set the sorted DataView as DataSource for the grid control\nyourGridControl.DataSource = dataTable.DefaultView;\n\n// Bind data to the grid control\nyourGridControl.DataBind();