private List<ComboBox> dayBoxes = new List<ComboBox>(); // New list to hold references of ComboBoxes\nprivate int howMany = 0;\nprivate int dayLastTop = 0; // Initial offset if needed\n\nprivate void addMoreBtn_Click(object sender, EventArgs e)\n{\n    howMany++;\n    ComboBox dayBox = new System.Windows.Forms.ComboBox();\n    dayBox.Location = new System.Drawing.Point(13, 75 + dayLastTop);\n    dayBox.Name = "dayBox" + howMany.ToString();\n    dayBox.TabIndex = howMany;\n    dayBox.Visible = true;\n    dayBox.Text = "Pick One";\n    \n    foreach (var item in dayBoxO.Items)\n    {\n        dayBox.Items.Add(item);\n    }\n\n    this.Controls.Add(dayBox);\n    dayBoxes.Add(dayBox); // Add the ComboBox to the list\n    dayLastTop = dayBox.Top - 49;\n}\n\npublic void saveToFile()\n{\n    FileInfo file = new FileInfo("Workout Log.txt");\n    using (StreamWriter writer = file.CreateText())\n    {\n        writer.WriteLine("---------------Workout Buddy Log---------------");\n        writer.WriteLine("------------------- " + DateTime.Now.ToShortDateString() + " ------------------");\n        writer.WriteLine();\n\n        foreach (var dayBox in dayBoxes)\n        {\n            if (!string.Equals(dayBox.Text, "Pick One", StringComparison.OrdinalIgnoreCase))\n            {\n                writer.WriteLine("Day: " + dayBox.Text);\n            }\n            else\n            {\n                writer.WriteLine("Day: N/A");\n            }\n        }\n    }\n}