// Assuming you have an ID for Master1\nint master1Id = 1; // Replace with your specific Master1 ID\n\n// Query the join table first to get all Sub1 IDs related to this Master1.ID\nvar sub1Ids = new Select(Master1Sub1_Map.Columns.Sub1ID)\n    .From(Master1Sub1_Map.Schema)\n    .Where(Master1Sub1_Map.Columns.Master1ID).IsEqualTo(master1Id)\n    .ExecuteTypedList<int>();\n\n// Use the collected Sub1 IDs to fetch Sub1 records\nvar sub1Records = new Select().From(Sub1.Schema)\n    .In(Sub1.Columns.ID, sub1Ids)\n    .ExecuteCollection<Sub1>();