protected void MyTextBox_TextChanged(object sender, EventArgs e)\n{\n    // Get the value from the TextBox\n    string input = MyTextBox.Text.Trim();\n\n    // Try to parse the decimal value\n    if (decimal.TryParse(input, out decimal result))\n    {\n        // If parsing is successful, update the item with the valid decimal value\n        MyFormView.UpdateItem(false);\n    }\n    else\n    {\n        // If parsing fails, set the value to null and update the item\n        var dataKeyValue = MyFormView.DataKey.Value;\n        var myTypeInstance = YourDataContext.MyTypes.FirstOrDefault(item => item.YourDataKey == (int)dataKeyValue);\n\n        if (myTypeInstance != null)\n        {\n            myTypeInstance.MyValue = null;\n            YourDataContext.SubmitChanges();\n            MyFormView.DataBind();\n        }\n    }\n}