protected void Application_PostAuthenticateRequest(object sender, EventArgs e)\n{\n    // Check if the roles are already populated or not.\n    var identity = HttpContext.Current.User.Identity;\n    \n    if (identity.IsAuthenticated && !Roles.IsUserInRole(identity.Name, "Users"))\n    {\n        // Add user to the 'Users' role if they are not already part of it.\n        Roles.AddUserToRole(identity.Name, "Users");\n    }\n}