using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string fileName = "cheese.exe";\n        string[] drives = Environment.GetLogicalDrives();\n\n        foreach (string drive in drives)\n        {\n            try\n            {\n                string path = FindFile(drive, fileName);\n                if (!string.IsNullOrEmpty(path))\n                {\n                    Console.WriteLine("Found at: " + path);\n                    break; // Exit loop once the file is found\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"An error occurred: {ex.Message}");\n            }\n        }\n    }\n\n    static string FindFile(string rootDirectory, string fileName)\n    {\n        foreach (string directory in Directory.EnumerateDirectories(rootDirectory))\n        {\n            try\n            {\n                foreach (string file in Directory.GetFiles(directory, fileName))\n                {\n                    return file;\n                }\n\n                string foundPath = FindFile(directory, fileName);\n                if (!string.IsNullOrEmpty(foundPath))\n                {\n                    return foundPath;\n                }\n            }\n            catch { /* Ignore access errors */ }\n        }\n\n        return null; // Return null if the file is not found\n    }\n}