using System;\nusing System.Net.Http;\nusing System.Text.Json;\nusing System.Threading.Tasks;\n\npublic class RestApiClient\n{\n    private readonly HttpClient client;\n\n    public RestApiClient()\n    {\n        client = new HttpClient();\n        // Configure the base address and default headers\n        client.BaseAddress = new Uri("http://your-java-server/api/");\n        client.DefaultRequestHeaders.UserAgent.ParseAdd("C# Rest Client");\n        client.DefaultRequestHeaders.Accept.Clear();\n        client.DefaultRequestHeaders.Accept.Add(new System.Net.Http.Headers.MediaTypeWithQualityHeaderValue("application/json"));\n    }\n\n    public async Task<string> GetDataFromServerAsync(string resource)\n    {\n        var response = await client.GetAsync(resource);\n        response.EnsureSuccessStatusCode(); // Throw on non-success HTTP responses\n        return await response.Content.ReadAsStringAsync();\n    }\n}