using System;\nusing System.IO;\nusing System.Text;\n\npublic void WriteAnsiToFile(string filePath, string content)\n{\n    using (FileStream fileStream = new FileStream(filePath, FileMode.Create, FileAccess.Write))\n    {\n        using (StreamWriter writer = new StreamWriter(fileStream, Encoding.GetEncoding("Windows-1252")))\n        {\n            writer.Write(content);\n        }\n    }\n}