protected void Page_Load(object sender, EventArgs e)\n{\n    string htmlContent = "<p>Some HTML content</p>"; // This would likely be dynamically generated\n    int someNumber = 42; // The number you want to return\n\n    var response = new { Html = htmlContent, Number = someNumber };\n\n    Response.Clear();\n    Response.ContentType = "application/json";\n    Response.Write(JsonConvert.SerializeObject(response));\n    Response.End();\n}