using (SqlConnection connection = new SqlConnection(connectionString))\n{\n    SqlCommand command = new SqlCommand("SearchMoviesByTitle", connection);\n    command.CommandType = CommandType.StoredProcedure;\n\n    // Add parameters to the command.\n    command.Parameters.AddWithValue("@SearchTerm1", "hollywood");\n    command.Parameters.AddWithValue("@SearchTerm2", "square");\n\n    try\n    {\n        connection.Open();\n        SqlDataReader reader = command.ExecuteReader();\n\n        while (reader.Read())\n        {\n            Console.WriteLine(reader["TITLE"].ToString());\n        }\n        reader.Close();\n    }\n    catch (Exception ex)\n    {\n        Console.WriteLine("Exception: {0}", ex.Message);\n    }\n}