using Microsoft.AspNetCore.Mvc;\n\n[ApiController]\n[Route("api/[controller]")]\npublic class YourController : ControllerBase\n{\n    [HttpGet("{id}")]\n    public IActionResult Get(int id)\n    {\n        // Logic to fetch your object by id\n        var yourObject = GetYourObjectById(id);\n\n        if (yourObject == null)\n        {\n            return NotFound();\n        }\n\n        return Ok(yourObject); // Serializes the object to JSON automatically\n    }\n\n    private YourObjectType GetYourObjectById(int id)\n    {\n        // Implementation to retrieve your object based on ID\n        return new YourObjectType();\n    }\n}