using System;\nusing System.Runtime.InteropServices;\n\npublic class DisplayController\n{\n    [DllImport("user32.dll", CharSet = CharSet.Auto)]\n    private static extern IntPtr SendMessage(IntPtr hWnd, uint Msg, IntPtr wParam, IntPtr lParam);\n\n    // Constants for Monitor Control Messages\n    private const int SC_MONITORPOWER = 0xF170;\n    private const int WM_SYSCOMMAND = 0x112;\n\n    public enum DisplayState : int\n    {\n        On = -1,\n        Off = 2,\n        StandBy = 1\n    }\n\n    public static void SetDisplayState(DisplayState state)\n    {\n        SendMessage(\n            IntPtr.Zero, \n            WM_SYSCOMMAND, \n            (IntPtr)(SC_MONITORPOWER | ((int)state << 16)), \n            IntPtr.Zero);\n    }\n}\n\n// Usage\npublic class MyClass\n{\n    public static void Main()\n    {\n        DisplayController.SetDisplayState(DisplayState.Off); // Turn off display\n    }\n}