using System;\nusing System.Collections.Generic;\nusing System.Data.SqlClient;\n\npublic class WallPostDataAccess\n{\n    private string connectionString = "your_connection_string";\n\n    public List<WallPost> GetPosts()\n    {\n        List<WallPost> posts = new List<WallPost>();\n        using (SqlConnection conn = new SqlConnection(connectionString))\n        {\n            SqlCommand cmd = new SqlCommand("SELECT * FROM WallPosts", conn);\n            conn.Open();\n            SqlDataReader reader = cmd.ExecuteReader();\n\n            while (reader.Read())\n            {\n                posts.Add(new WallPost\n                {\n                    PostID = Convert.ToInt32(reader["PostID"]),\n                    UserID = Convert.ToInt32(reader["UserID"]),\n                    PostText = reader["PostText"].ToString(),\n                    PostDate = Convert.ToDateTime(reader["PostDate"])\n                });\n            }\n        }\n\n        return posts;\n    }\n\n    public void AddPost(WallPost post)\n    {\n        using (SqlConnection conn = new SqlConnection(connectionString))\n        {\n            string query = "INSERT INTO WallPosts(UserID, PostText) VALUES (@UserID, @PostText)";\n            SqlCommand cmd = new SqlCommand(query, conn);\n            cmd.Parameters.AddWithValue("@UserID", post.UserID);\n            cmd.Parameters.AddWithValue("@PostText", post.PostText);\n\n            conn.Open();\n            cmd.ExecuteNonQuery();\n        }\n    }\n\n    // Similarly implement UpdatePost and DeletePost methods\n}\n\npublic class WallPost\n{\n    public int PostID { get; set; }\n    public int UserID { get; set; }\n    public string PostText { get; set; }\n    public DateTime PostDate { get; set; }\n}