// Ensure application pool loads user profile (set in IIS)\n// Example code to demonstrate setting advanced properties programmatically,\n// but actual configuration should be done through IIS Manager\n\nusing System;\nusing Microsoft.Web.Administration;\n\nclass Program\n{\n    static void Main()\n    {\n        using (ServerManager serverManager = new ServerManager())\n        {\n            ApplicationPool pool = serverManager.ApplicationPools["YourAppPoolName"];\n            if (pool != null)\n            {\n                pool.ManagedRuntimeVersion = "v4.0";\n                // Ensure Load User Profile is set to true\n                pool.SetAttributeValue("processModel.loadUserProfile", true);\n                serverManager.CommitChanges();\n            }\n        }\n    }\n}