using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class ConfigEncryption\n{\n    private static readonly byte[] Key = Encoding.UTF8.GetBytes("superSecretKey1234"); // Replace with a strong key\n    private static readonly byte[] IV = Encoding.UTF8.GetBytes("InitializationV10!");  // Initialization vector\n\n    public static string EncryptString(string plainText)\n    {\n        using (Aes aesAlg = Aes.Create())\n        {\n            aesAlg.Key = Key;\n            aesAlg.IV = IV;\n\n            ICryptoTransform encryptor = aesAlg.CreateEncryptor(aesAlg.Key, aesAlg.IV);\n\n            using (MemoryStream msEncrypt = new MemoryStream())\n            {\n                using (CryptoStream csEncrypt = new CryptoStream(msEncrypt, encryptor, CryptoStreamMode.Write))\n                {\n                    using (StreamWriter swEncrypt = new StreamWriter(csEncrypt))\n                    {\n                        swEncrypt.Write(plainText);\n                    }\n\n                    return Convert.ToBase64String(msEncrypt.ToArray());\n                }\n            }\n        }\n    }\n\n    public static string DecryptString(string cipherText)\n    {\n        using (Aes aesAlg = Aes.Create())\n        {\n            aesAlg.Key = Key;\n            aesAlg.IV = IV;\n\n            ICryptoTransform decryptor = aesAlg.CreateDecryptor(aesAlg.Key, aesAlg.IV);\n\n            using (MemoryStream msDecrypt = new MemoryStream(Convert.FromBase64String(cipherText)))\n            {\n                using (CryptoStream csDecrypt = new CryptoStream(msDecrypt, decryptor, CryptoStreamMode.Read))\n                {\n                    using (StreamReader srDecrypt = new StreamReader(csDecrypt))\n                    {\n                        return srDecrypt.ReadToEnd();\n                    }\n                }\n            }\n        }\n    }\n\n    public static void Main()\n    {\n        string configData = "Server=myServerAddress;Database=myDataBase;Uid=myUsername;Pwd=myPassword;";\n        string encrypted = EncryptString(configData);\n        Console.WriteLine("Encrypted: {0}", encrypted);\n\n        string decrypted = DecryptString(encrypted);\n        Console.WriteLine("Decrypted: {0}", decrypted);\n    }\n}