using System;\n\nclass Program\n{\n    // Define a delegate with the specified signature\n    public delegate string MessageDelegate(int min, int max);\n\n    public static void Main()\n    {\n        // Create instances of the delegate pointing to different methods\n        MessageDelegate weightChecker = WeightInvalid;\n        MessageDelegate heightChecker = HeightInvalid;\n\n        // Use the delegates\n        Console.WriteLine(weightChecker(50, 100)); // Output: "Weight must be between 50 and 100"\n        Console.WriteLine(heightChecker(150, 200)); // Output: "Height must be between 150 and 200"\n    }\n\n    // Method that matches the delegate signature\n    public static string WeightInvalid(int min, int max)\n    {\n        return $"Weight must be between {min} and {max}";\n    }\n\n    // Another method that matches the delegate signature\n    public static string HeightInvalid(int min, int max)\n    {\n        return $"Height must be between {min} and {max}";\n    }\n}