protected void Page_Load(object sender, EventArgs e)\n{\n    // Check if a session is new or existing\n    if (Session.IsNewSession)\n    {\n        Response.Write("This is a new session.");\n    }\n    else\n    {\n        Response.Write("This session already exists.");\n    }\n\n    // Display the Session ID\n    string sessionId = Session.SessionID;\n    Response.Write($"<br>Session ID: {sessionId}");\n}