// Example of creating a simple RESTful Web API in C# using ASP.NET Core\n\nusing Microsoft.AspNetCore.Mvc;\n\n[ApiController]\n[Route("api/[controller]")]\npublic class DataController : ControllerBase\n{\n    [HttpGet]\n    public IActionResult GetData()\n    {\n        // Logic to fetch data from your database\n        var data = new { ID = 1, Name = "SampleData" };\n        return Ok(data);\n    }\n\n    [HttpPost]\n    public IActionResult PostData([FromBody] object newData)\n    {\n        // Logic to store data in your database\n        return CreatedAtAction(nameof(GetData), new { id = 1 }, newData);\n    }\n}