using System;\nusing System.IO;\nusing System.Text;\nusing System.Web;\n\npublic void WriteAnsiToResponse(string filePath, HttpResponse response)\n{\n    using (FileStream fileStream = new FileStream(filePath, FileMode.Open, FileAccess.Read))\n    {\n        using (StreamReader reader = new StreamReader(fileStream, Encoding.GetEncoding("Windows-1252")))\n        {\n            string content = reader.ReadToEnd();\n            response.ContentEncoding = Encoding.GetEncoding("Windows-1252");\n            response.ContentType = "text/plain"; // or "application/octet-stream" if it's a binary file\n            response.Write(content);\n        }\n    }\n}